window.addEventListener("DOMContentLoaded",()=>{"use strict";AOS.init();document.addEventListener("scroll",()=>{if(window.screen.availWidth>992){const e=document.querySelector(".header__menu");(window.pageYOffset?window.pageYOffset:document.documentElement.scrollTop?document.documentElement.scrollTop:document.body.scrollTop)>=1?e.classList.add("stiky"):e.classList.remove("stiky")}});new Swiper(".swiper-container",{direction:"vertical",slidesPerView:"auto",navigation:{nextEl:".article__next",prevEl:".article__prev"},grabCursor:!0});((e,t)=>{const c=document.querySelector(e),o=document.querySelector(t);c.addEventListener("click",()=>{c.classList.toggle("active"),o.classList.toggle("active"),c.classList.contains("active")?document.body.classList.add("lock"):document.body.classList.remove("lock")})})(".header__burger",".header__list"),((e,t)=>{if(window.screen.availWidth>=992){const c=document.querySelectorAll(e),o=document.querySelector(t);c.forEach(e=>{e.addEventListener("click",()=>{o.src=e.src.slice(21),o.previousElementSibling.srcset=e.src.slice(21)})})}})(".product__img",".product__img-now")});